import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";

export default function Product(props) {
  const { product, onAdd } = props;
  // return (
  //   <div>
  //     <h3>{product.name}</h3>
  //     <div>${product.price}</div>
  //     <div>
  //       <button onClick={() => onAdd(product)}>Add To Cart</button>
  //     </div>
  //   </div>
  // );
  return (
    <Card className="propCard shadow-sm bg-white rounded">
        <Card.Body className="bodyCards">
            <div className="float-container">
                <div className="col-lg-5 float-child">{this.props.name}</div>
                <div className="col-lg-4 float-child text-center">Price: <span className="colorPrice">${this.props.price}</span></div>
                <div className="col-lg-3 float-child text-center">
                    <Button variant="success" onClick={this.handleClick}>
                        <ShoppingCartIcon /> Add to Cart
                    </Button>
                </div>
            </div>
            <br />
        </Card.Body>
    </Card>
);
}
