import React from 'react';
import './Basket.css';

export default function Basket(props) {
  const { cartItems, onAdd, onRemove } = props;
  const itemsPrice = cartItems.reduce((a, c) => a + c.qty * c.price, 0);
  const totalPrice = itemsPrice;
  return (
    <aside className="block col-4 basket">
      <div className="card bg-light">
        <div className="card-body">
          {cartItems.length === 0 && <div className="titleCart"><h4>No products in your shopping cart</h4></div>}
          {cartItems.length !== 0 && <div className="titleCart"><h4>Products in your shopping cart</h4></div>}
          <br />
          {cartItems.length !== 0 && <div className="titleCart"><h4>Products in your shopping cart</h4></div>}
          {cartItems.map((item) => (
            <div key={item.id} className="row">
              <div className="col-4">{item.name}</div>
              <div className="col-4">
                <button onClick={() => onRemove(item)} className="remove">
                  -
                </button>{' '}
                <button onClick={() => onAdd(item)} className="add">
                  +
                </button>
              </div>

              <div className="col-4 text-right">
                {item.qty} x ${item.price.toFixed(2)}
              </div>
            </div>
          ))}

          {cartItems.length !== 0 && (
            <>
              <br />
              <div className="row">
                <div className="col-4">
                  <strong>Total Price</strong>
                </div>
                <div className="col-4"></div>
                <div className="col-4 text-right">
                  <strong>${totalPrice.toFixed(2)}</strong>
                </div>
              </div>
              <hr className="hrBasket"/>
              <div className="text-center">
                <button className="btn btn-success" onClick={() => alert('Implement Checkout!')}>
                  Continue
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </aside>

  );
}
